package Jan31st;

import java.util.Scanner;

public class ProductofDecimalAndInteger {

	public static void main(String[] args) {
		// TODO Auto-generated method 
		Scanner sc=new Scanner(System.in);
				
		float a=sc.nextFloat();
		int b=sc.nextInt();
		float p=a*b;
		System.out.println(p);
		

	}

}
