package feb4th;

public class AreaAndPerimeterOfRectangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int l=5;
		int b=7;
		int area=(l*b);
		int perimeter=2*(l+b);
		System.out.println("area of rectngle="+area);

		System.out.print("perimeter of rectngle="+perimeter);

	}

}
